// src/app/services/survey.service.ts

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Survey } from "../models/survey.model";

@Injectable({
  providedIn: 'root'
})
export class SurveyService {
  private apiUrl = 'http://localhost:8080/api/surveys'; // Adjust if your API endpoint differs

  constructor(private http: HttpClient) { }

  submitSurvey(survey: Survey): Observable<Survey> {
    return this.http.post<Survey>(this.apiUrl, survey);
  }

  // Additional service methods can be implemented as needed, e.g., to retrieve surveys
}
